const compareObjects = (objectFirst, ObjectSecond, search) => {
	  let tmpObje = [];
	  
	   for(obj1 of objectFirst){
		   tmpObj.push(obj1);
	   }
	   
	   for(obj2 of ObjectSecond){
		   tmpObj.push(obj2);
	   }
	  
	  return [...new Map(tmpObj.map(item => [item[search], item])).values()];
  }
  
  
 

  module.exports = compareObjects